hudson.tasks.Shell.command:
#!/usr/bin/python
import json
import os
json_file = open("config.json")
config = json.loads(json_file.read())
json_file.close()
if "install-type" not in config["eucalyptus"]:
    config["eucalyptus"]["install-type"] = os.environ['method']
config["eucalyptus"]["topology"] = {}
config["eucalyptus"]["topology"]["clusters"] = {}
config["eucalyptus"]["topology"]["clusters"]["default"] = {}
config["eucalyptus"]["topology"]["clusters"]["default"]["cc-1"] = os.environ['cluster']
config["eucalyptus"]["topology"]["clusters"]["default"]["nodes"] = os.environ['frontend']
json_file = open("config.json", 'w')
json_file.write(json.dumps(config))
